import React from 'react';

import { Carousel } from 'react-responsive-carousel';
import axios from 'axios';

class CarouselComponent extends React.Component {
	state = {
		movies: [],
    images: [],
    titles:[],
    movieID:[],
    original_title:[],
    tagline:[],
    overview:[],
    homepage:[],
    poster:[],
    production:[],
    production_countries:[],
    genre:[],
    release: movies.cast[i].release_date,
    vote:movies.cast[i].vote_average,
    runtime: movies.cast[i].runtime,
    revenue: movies.cast[i].revenue,
    backdrop: movies.cast[i].backdrop_path
  };
  

	async componentDidMount() {
		const { data: movies } = await axios.get(
			' https://api.themoviedb.org/3/person/85/movie_credits?api_key=' +
				process.env.REACT_APP_API_KEY +
				'&sort_by=release_date.desc'
		);
		this.setState({ movies: movies.cast });

    let movieImageUrls = [];
    let movieTitles = []
		let trendingMoviesCount = this.state.movies.length;
		for (let i = 0; i < trendingMoviesCount; i++) {
			const posterPath = 'https://image.tmdb.org/t/p/w500';
      movieImageUrls.push(posterPath + movies.cast[i].poster_path);
      movieTitles.push(this.state.movies[i].original_title)
      this.setState({ 
        images: movieImageUrls,
        titles: movieTitles,
        movieID: movies.cast[i].id,
        original_title: movies.cast[i].original_title,
        tagline: movies.cast[i].tagline,
        overview: movies.cast[i].overview,
        homepage:movies.cast[i].homepage,
        poster: movies.cast[i].poster_path,
        production:movies.cast[i].production_companies,
        production_countries: movies.cast[i].production_countries,
        genre:movies.cast[i].genres,
        release: movies.cast[i].release_date,
        vote:movies.cast[i].vote_average,
        runtime: movies.cast[i].runtime,
        revenue: movies.cast[i].revenue,
        backdrop: movies.cast[i].backdrop_path
       });

     
		}  console.log(this.state)
	
	}

	

	render() {
    const { images } = this.state;
    let {movieTitleToDisplay} = this.state.titles

		if (!images) return <div>Please wait while images load....</div>;
    
		// #3. Finally, render the `<Carousel />` with the state's images.
		return (
			<Carousel autoPlay infiniteLoop="true" dots="false" showIndicators={false}>
				{images.map((image) => {
					return (
						<div>
							<img src={image} alt="Johnny Covers" />
          
					
						</div>
					);
				})}
        <p>{movieTitleToDisplay}</p>
			</Carousel>
      
		);
	}
}
export default CarouselComponent;
